import './ChatScreen.css';
import './LeftScreen.css';
import users from '../db/UsersDataBase';
import { useState } from "react";
import LeftChatItem from './LeftChatItem';import conversations from '../db/Conversations';
;

function LeftScreen({ logInUsername }) {
    let numOfConversations = 0;
    // function getJasonObject({ username }) {
    //     for (var i = 0; i < users.length; i++) {
    //         if (users[i].username.localeCompare("Ofek Koren")==0) {
    //             numOfConversations = users[i].chats.length;
    //             return users[i];
    //         }
    //     }
    // }
    function getJasonObject({ username }) {
        for (var i = 0; i < users.length; i++) {
            if (users[i].username.localeCompare("Ofek Koren")==0) {
                numOfConversations = users[i].chats.length;
                return users[i].chats;
            }
        }
    }
    var conversations = [];
    var chats=getJasonObject(logInUsername);
    // chats = JSON.stringify(chats);

    for(var i = 0; i < chats.length; i++) {
        conversations.push(chats[i]);
    }
    var relevantInfo = [];
    var usernameInChat = "";
    var lastMessage = "";
    var time = "";
    for(var i = 0; i < conversations.length; i++) {
        if(chats[i].users.at[0].username.localeCompare(logInUsername)==0){
            usernameInChat = chats[i].users.at[0].username;
        } else {
            usernameInChat = chats[i].users.at[1].username;
        }
        lastMessage = chats[i].massages[chats[i].massages.length-1].content;
        time = chats[i].massages[chats[i].massages.length-1].time;
        relevantInfo.push({usernameInChat: {usernameInChat}, lastMessage: {lastMessage}, time: {time}});
    }
    // conversationsList = relevantInfo.map((conversation,key)=> {
    //     return <LeftChatItem {...conversation} key={key}/>
    // });

    // const conversations = [{chatUsername: "", lastMessage: "", time: ""},{chatUsername: "", lastMessage: "", time: ""}];
    return (
        <div className="col-4 leftScreen">
            <div className="topLine topLine-left">
                <span className="bi bi-person-plus-fill add-conversation ms-3"></span>
                <img src={require('../images/userImages/boy-image.png')} className="rounded float-start top-profile-image top-left-profile-image"></img>
                <h5 className='top-left-username'>{username}</h5>
            </div>
            {/* <LeftChatItem chat={getJasonObject(username)} /> */}
            {/* <LeftChatItem chat={{ currentUsername: "username", lastMessage: "Hey", time: "5" }} />
            <LeftChatItem chat={{ currentUsername: "username", lastMessage: "Hey", time: "5" }} />
            <LeftChatItem chat={{ currentUsername: "username", lastMessage: "Hey", time: "5" }} /> */}
            {/* {relevantInfo} */}
        </div>

    );
}

export default LeftScreen;
