import conversations from '../db/Conversations';
import users from '../db/UsersDataBase';

// function ChooseNewChat({logInUsername, conversationsList}) {
function ChooseNewChat() {
    // // getUsername = (event)=>{
    // //     const userValue = event.target.value;
    // //     console.log(userValue);
    // // };
    // function getUsername(event) {
    //     const userValue = event.target.value;
    //     console.log(userValue);
    // };

    return (
        <div className="col-4 leftScreen">
            <div class="modal fade" id="add-new-contact-model" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Add new contact</h5>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body">
                            <form>
                                <label for="floatingTextarea" class="col-form-label">Please enter the contact's username:</label>
                                <div class="form-floating">
                                    {/* <textarea class="form-control" placeholder="Leave a comment here" id="floatingTextarea" onChange={getUsername}></textarea> */}
                                    <textarea class="form-control" placeholder="Leave a comment here" id="floatingTextarea"></textarea>

                                    <label for="floatingTextarea">Contact's identifier</label>
                                </div>
                            </form>
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                            {/* <button type="button" class="btn btn-primary" onClick={ad}>Add</button> */}
                            <button type="button" class="btn btn-primary">Add</button>

                        </div>
                    </div>
                </div>
            </div>

        </div>

    );
}

export default ChooseNewChat;
