import './LeftChatItem.css';
import React from 'react';


function LeftChatItem(conversation) {

    return (
        <div className='left-chat-item'>
            <img src={require('../images/userImages/boy-image.png')} className="rounded float-start top-left-profile-image"></img>
            {/* <img src={chat.image} className="rounded float-start top-left-profile-image"></img> */}
            <h5 className="chat-member-name">{conversation.props.usernameInChat[0]}</h5>
            <span className="chat-last-message">{conversation.props.lastMessage[0]}</span>
            <span className='last-message-time'>{conversation.time[0]} minutes gao</span>
        </div>
    );
}
export default LeftChatItem;
